mapper.insert:

select NVl(max(bno), 100) + 1
from board

insert into board (
                    bno,
                    title,
                    contents,
                    writer,
                    regdate,
                    image
                   )
        values (
        		#{bno}
        		, #{title}
        		, #{contents}
        		, #{writer}
        		, #{regdate}
        		, #{image}
               )

mapper.update:
update board
set title = #{title}
			, contents = #{contents}
			, writer = #{writer}
			, updatedate = sysdate
			, image = #{image}

where bno = #{bno}
